/**
 * ColorSwitchCN Component
 * Inspired by and compatible with TweakCN (https://tweakcn.com) by Sahaj (https://github.com/jnsahaj)
 */
"use client";

import * as React from "react";
import { ThemeModeToggle } from "./theme-mode-toggle";
import { ThemePicker } from "./theme-picker";
import { cn } from "@/lib/utils";

interface ColorThemeSwitcherProps {
	className?: string;
	align?: "start" | "center" | "end";
}

export function ColorThemeSwitcher({
	className,
	align = "center",
}: ColorThemeSwitcherProps) {
	const alignClass = {
		start: "justify-start",
		center: "justify-center",
		end: "justify-end",
	};

	// Pass down theme mode toggle props
	const themeModeToggleProps = {
		variant: "outline" as const,
		size: "icon" as const,
	};

	return (
		<div className={cn("flex flex-row gap-4", alignClass[align], className)}>
			<ThemeModeToggle {...themeModeToggleProps} />
			<ThemePicker />
		</div>
	);
}

"use client";

import type * as React from "react";
import { Moon, Sun } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useTheme } from "@/lib/theme-provider";

export interface ThemeModeToggleProps {
	variant?: "default" | "outline" | "ghost";
	size?: "default" | "sm" | "lg" | "icon";
	className?: string;
}

export function ThemeModeToggle({
	variant = "outline",
	size = "icon",
	className = "",
}: ThemeModeToggleProps) {
	const { themeState, setThemeMode } = useTheme();
	const isDarkMode = themeState.currentMode === "dark";

	const toggleTheme = () => {
		const newMode = isDarkMode ? "light" : "dark";
		setThemeMode(newMode);
	};

	return (
		<Button
			variant={variant}
			size={size}
			onClick={toggleTheme}
			aria-label={`Switch to ${isDarkMode ? "light" : "dark"} theme`}
			className={`transition-all hover:scale-105 active:scale-95 ${className}`}
		>
			{isDarkMode ? (
				<Sun className="h-[1.2rem] w-[1.2rem]" />
			) : (
				<Moon className="h-[1.2rem] w-[1.2rem]" />
			)}
		</Button>
	);
}

"use client";

import * as React from "react";
import { Check, ChevronDown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { ColorSwatch } from "@/components/ui/color-swatch";
import {
	DropdownMenu,
	DropdownMenuContent,
	DropdownMenuItem,
	DropdownMenuTrigger,
	DropdownMenuSeparator,
	DropdownMenuLabel,
} from "@/components/ui/dropdown-menu";
import { useTheme } from "@/lib/theme-provider";
import { presets } from "@/lib/theme-presets";
import { cn } from "@/lib/utils";
import type { ThemeStyleProps } from "@/lib/types";

export function ThemePicker() {
	const { themeState, applyThemePreset } = useTheme();
	const currentPreset = themeState.preset;
	const mode = themeState.currentMode;
	const [error, setError] = React.useState<string | null>(null);

	// Get preset display name
	const getPresetLabel = (presetKey: string) => {
		return (
			presets[presetKey]?.label ||
			presetKey
				.split("-")
				.map((word) => word.charAt(0).toUpperCase() + word.slice(1))
				.join(" ")
		);
	};

	// Get safe color value, always returning a string even if undefined
	const getSafeColor = (
		styles: ThemeStyleProps,
		property: keyof ThemeStyleProps,
	) => {
		if (!styles || !styles[property]) return "#cccccc";
		return styles[property] as string;
	};

	// Apply theme preset with error handling
	const handlePresetChange = (preset: string) => {
		try {
			console.log(`ThemePicker: Applying preset ${preset}`);
			applyThemePreset(preset);
			setError(null);
		} catch (err) {
			console.error("Error applying theme preset:", err);
			setError(
				err instanceof Error ? err.message : "Unknown error applying theme",
			);
		}
	};

	return (
		<DropdownMenu>
			<DropdownMenuTrigger asChild>
				<Button variant={"outline"} className="flex items-center gap-2">
					<div className="flex gap-1">
						<ColorSwatch
							color={getSafeColor(themeState.styles[mode], "primary")}
							size="sm"
						/>
						<ColorSwatch
							color={getSafeColor(themeState.styles[mode], "accent")}
							size="sm"
						/>
						<ColorSwatch
							color={getSafeColor(themeState.styles[mode], "secondary")}
							size="sm"
						/>
					</div>
					<span className="hidden sm:inline-block">
						{getPresetLabel(currentPreset)}
					</span>
					<ChevronDown className="h-4 w-4 text-muted-foreground" />
				</Button>
			</DropdownMenuTrigger>
			<DropdownMenuContent
				align="end"
				className="w-[220px] bg-background shadow-lg rounded-md max-h-[300px] overflow-y-auto"
			>
				{error && (
					<>
						<DropdownMenuLabel className="text-destructive">
							Error: {error}
						</DropdownMenuLabel>
						<DropdownMenuSeparator />
					</>
				)}

				{Object.keys(presets).map((presetKey) => {
					const isActive = currentPreset === presetKey;
					const styles = presets[presetKey].styles[mode] || {};

					return (
						<DropdownMenuItem
							key={presetKey}
							className={cn(
								"flex items-center gap-2 cursor-pointer",
								isActive && "font-medium bg-accent text-accent-foreground",
							)}
							onClick={() => handlePresetChange(presetKey)}
						>
							<div className="flex gap-1">
								<ColorSwatch
									color={getSafeColor(styles, "primary")}
									size="sm"
								/>
								<ColorSwatch color={getSafeColor(styles, "accent")} size="sm" />
								<ColorSwatch
									color={getSafeColor(styles, "secondary")}
									size="sm"
								/>
							</div>
							<span>{getPresetLabel(presetKey)}</span>
							{isActive && <Check className="h-4 w-4 ml-auto" />}
						</DropdownMenuItem>
					);
				})}
			</DropdownMenuContent>
		</DropdownMenu>
	);
}

"use client";

import type * as React from "react";
import { cn } from "@/lib/utils";

interface ColorSwatchProps extends React.HTMLAttributes<HTMLDivElement> {
	color: string;
	size?: "sm" | "md" | "lg";
}

export function ColorSwatch({
	color,
	size = "md",
	className,
	...props
}: ColorSwatchProps) {
	const sizeClasses = {
		sm: "h-4 w-4",
		md: "h-6 w-6",
		lg: "h-8 w-8",
	};

	return (
		<div
			className={cn(
				"rounded-md flex-shrink-0 border border-border/30",
				sizeClasses[size],
				className,
			)}
			style={{ backgroundColor: color }}
			{...props}
		/>
	);
}

/**
 * Theme provider for ColorSwitchCN.
 * Implementation inspired by and compatible with TweakCN (https://tweakcn.com) by Sahaj (https://github.com/jnsahaj).
 */
"use client";

import React, { createContext, useContext, useEffect, useState } from "react";
import type { ThemeMode, ThemeState, ThemeProviderProps } from "./types";
import { defaultThemeState, getPresetThemeStyles } from "./theme-presets";
import { applyTheme } from "./fix-global-css";

// Theme context type
type ThemeContextType = {
	themeState: ThemeState;
	setThemeMode: (mode: ThemeMode) => void;
	applyThemePreset: (preset: string) => void;
};

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

// Custom theme provider component
export function CustomThemeProvider({
	children,
	defaultPreset,
}: ThemeProviderProps) {
	// State to track theme
	const [themeState, setThemeState] = useState<ThemeState>(defaultThemeState);
	const [isInitialized, setIsInitialized] = useState(false);

	// Initialize theme from localStorage or system preference if available
	useEffect(() => {
		if (typeof window === "undefined") return;

		try {
			const savedTheme = localStorage.getItem("theme-mode") as ThemeMode | null;
			const savedPreset =
				localStorage.getItem("theme-preset") || defaultPreset || "default";

			// System preference for dark mode
			const prefersDark = window.matchMedia(
				"(prefers-color-scheme: dark)",
			).matches;
			const initialMode = savedTheme || (prefersDark ? "dark" : "light");

			// Set initial theme state
			const initialState = {
				currentMode: initialMode,
				preset: savedPreset,
				styles: getPresetThemeStyles(savedPreset),
			};

			console.log("Initializing theme:", initialState);
			setThemeState(initialState);
			setIsInitialized(true);

			// Apply initial theme styles
			applyTheme(initialState);
		} catch (error) {
			console.error("Error initializing theme:", error);
			// Fallback to default theme
			setThemeState(defaultThemeState);
			setIsInitialized(true);
			applyTheme(defaultThemeState);
		}
	}, [defaultPreset]);

	// Apply theme whenever it changes
	useEffect(() => {
		if (typeof window === "undefined" || !isInitialized) return;

		try {
			// Save preferences
			localStorage.setItem("theme-mode", themeState.currentMode);
			localStorage.setItem("theme-preset", themeState.preset);

			// Apply the theme styles
			applyTheme(themeState);

			console.log(
				`Theme applied: ${themeState.preset}, mode: ${themeState.currentMode}`,
				themeState,
			);
		} catch (error) {
			console.error("Error applying theme:", error);
		}
	}, [themeState, isInitialized]);

	// Change theme mode (light/dark)
	const setThemeMode = (mode: ThemeMode) => {
		console.log(`Setting theme mode: ${mode}`);
		setThemeState((prev) => ({
			...prev,
			currentMode: mode,
		}));
	};

	// Apply a theme preset
	const applyThemePreset = (preset: string) => {
		console.log(`Applying preset: ${preset}`);
		try {
			const newStyles = getPresetThemeStyles(preset);

			setThemeState((prev) => ({
				...prev,
				preset,
				styles: newStyles,
			}));
		} catch (error) {
			console.error(`Error applying preset ${preset}:`, error);
		}
	};

	// Provider value
	const value = {
		themeState,
		setThemeMode,
		applyThemePreset,
	};

	return (
		<ThemeContext.Provider value={value}>{children}</ThemeContext.Provider>
	);
}

// Hook to access theme context
export function useTheme(): ThemeContextType {
	const context = useContext(ThemeContext);
	if (context === undefined) {
		throw new Error("useTheme must be used within a ThemeProvider");
	}
	return context;
}

import type { ColorFormat } from "./types";

// Helper function to convert hex to RGB
function hexToRgb(hex: string): { r: number; g: number; b: number } | null {
	const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
	const expandedHex = hex.replace(
		shorthandRegex,
		(_, r, g, b) => r + r + g + g + b + b,
	);

	const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(expandedHex);
	return result
		? {
				r: Number.parseInt(result[1], 16),
				g: Number.parseInt(result[2], 16),
				b: Number.parseInt(result[3], 16),
			}
		: null;
}

// Helper function to convert RGB to hex
function rgbToHex(r: number, g: number, b: number): string {
	return `#${((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1)}`;
}

// Helper function to convert RGB to HSL
function rgbToHsl(
	r: number,
	g: number,
	b: number,
): { h: number; s: number; l: number } {
	const rNorm = r / 255;
	const gNorm = g / 255;
	const bNorm = b / 255;

	const max = Math.max(rNorm, gNorm, bNorm);
	const min = Math.min(rNorm, gNorm, bNorm);
	let h = 0;
	let s = 0;
	const l = (max + min) / 2;

	if (max !== min) {
		const d = max - min;
		s = l > 0.5 ? d / (2 - max - min) : d / (max + min);

		switch (max) {
			case rNorm:
				h = (gNorm - bNorm) / d + (gNorm < bNorm ? 6 : 0);
				break;
			case gNorm:
				h = (bNorm - rNorm) / d + 2;
				break;
			case bNorm:
				h = (rNorm - gNorm) / d + 4;
				break;
		}

		h /= 6;
	}

	return { h: h * 360, s: s * 100, l: l * 100 };
}

// Helper function to parse HSL string
function parseHsl(hslStr: string): { h: number; s: number; l: number } | null {
	const hslRegex = /hsl\(\s*(\d+)\s*,\s*(\d+)%\s*,\s*(\d+)%\s*\)/i;
	const hslRegexWithSpaces = /hsl\(\s*(\d+)\s+(\d+)%\s+(\d+)%\s*\)/i;

	const match = hslRegex.exec(hslStr) || hslRegexWithSpaces.exec(hslStr);

	if (!match) {
		const rawValues = hslStr.split(" ");
		if (rawValues.length === 3) {
			return {
				h: Number.parseFloat(rawValues[0]),
				s: Number.parseFloat(rawValues[1].replace("%", "")),
				l: Number.parseFloat(rawValues[2].replace("%", "")),
			};
		}
		return null;
	}

	return {
		h: Number.parseInt(match[1], 10),
		s: Number.parseInt(match[2], 10),
		l: Number.parseInt(match[3], 10),
	};
}

// Format color value to specified format
export function colorFormatter(
	color: string,
	targetFormat: ColorFormat,
	precision = "0",
): string {
	// Handle empty or invalid input
	if (!color) return "";

	// If color is already in the target format
	if (
		(targetFormat === "hex" && color.startsWith("#")) ||
		(targetFormat === "hsl" && color.startsWith("hsl")) ||
		(targetFormat === "rgb" && color.startsWith("rgb")) ||
		(targetFormat === "oklch" && color.startsWith("oklch"))
	) {
		return color;
	}

	// Extract RGB values from different formats
	let r: number;
	let g: number;
	let b: number;

	if (color.startsWith("#")) {
		// Convert from hex
		const rgb = hexToRgb(color);
		if (!rgb) return color;
		r = rgb.r;
		g = rgb.g;
		b = rgb.b;
	} else if (color.startsWith("rgb")) {
		// Parse RGB format
		const match = /rgba?\(\s*(\d+)\s*,\s*(\d+)\s*,\s*(\d+)/.exec(color);
		if (!match) return color;
		r = Number.parseInt(match[1], 10);
		g = Number.parseInt(match[2], 10);
		b = Number.parseInt(match[3], 10);
	} else if (color.startsWith("hsl") || color.includes(" ")) {
		// Parse HSL format
		const hsl = parseHsl(color);
		if (!hsl) return color;

		// Convert HSL to RGB
		const { h, s, l } = hsl;
		const c = ((1 - Math.abs((2 * l) / 100 - 1)) * s) / 100;
		const x = c * (1 - Math.abs(((h / 60) % 2) - 1));
		const m = l / 100 - c / 2;

		let rp: number;
		let gp: number;
		let bp: number;

		if (h >= 0 && h < 60) {
			[rp, gp, bp] = [c, x, 0];
		} else if (h >= 60 && h < 120) {
			[rp, gp, bp] = [x, c, 0];
		} else if (h >= 120 && h < 180) {
			[rp, gp, bp] = [0, c, x];
		} else if (h >= 180 && h < 240) {
			[rp, gp, bp] = [0, x, c];
		} else if (h >= 240 && h < 300) {
			[rp, gp, bp] = [x, 0, c];
		} else {
			[rp, gp, bp] = [c, 0, x];
		}

		r = Math.round((rp + m) * 255);
		g = Math.round((gp + m) * 255);
		b = Math.round((bp + m) * 255);
	} else {
		return color; // Return original if not recognized
	}

	// Convert to target format
	switch (targetFormat) {
		case "hex":
			return rgbToHex(r, g, b);
		case "rgb":
			return `rgb(${r}, ${g}, ${b})`;
		case "hsl": {
			const { h, s, l } = rgbToHsl(r, g, b);
			const hRounded = Math.round(h);
			const sRounded = Math.round(s);
			const lRounded = Math.round(l);

			if (precision === "0") {
				return `${hRounded} ${sRounded}% ${lRounded}%`;
			}

			return `hsl(${hRounded}, ${sRounded}%, ${lRounded}%)`;
		}
		case "oklch":
			// A simplified conversion to oklch format
			// This is a placeholder - real conversion would require proper color space transformations
			return `oklch(${(0.2126 * r + 0.7152 * g + 0.0722 * b) / 255} ${
				Math.sqrt((r - g) * (r - g) + (r - b) * (r - b) + (g - b) * (g - b)) /
				255 /
				3
			} ${(Math.atan2(b - g, r - g) * 180) / Math.PI})`;
		default:
			return color;
	}
}

export function applyStyleToElement(
	element: HTMLElement,
	property: string,
	value: string,
): void {
	try {
		element.style.setProperty(`--${property}`, value);
	} catch (error) {
		console.error(`Error setting CSS variable --${property}:`, error);
	}
}

export type ThemeMode = "light" | "dark";

export interface ThemeStyleProps {
	background?: string;
	foreground?: string;
	card?: string;
	"card-foreground"?: string;
	popover?: string;
	"popover-foreground"?: string;
	primary?: string;
	"primary-foreground"?: string;
	secondary?: string;
	"secondary-foreground"?: string;
	muted?: string;
	"muted-foreground"?: string;
	accent?: string;
	"accent-foreground"?: string;
	destructive?: string;
	"destructive-foreground"?: string;
	border?: string;
	input?: string;
	ring?: string;
	"chart-1"?: string;
	"chart-2"?: string;
	"chart-3"?: string;
	"chart-4"?: string;
	"chart-5"?: string;
	radius?: string;
	sidebar?: string;
	"sidebar-foreground"?: string;
	"sidebar-primary"?: string;
	"sidebar-primary-foreground"?: string;
	"sidebar-accent"?: string;
	"sidebar-accent-foreground"?: string;
	"sidebar-border"?: string;
	"sidebar-ring"?: string;
	"font-sans"?: string;
	"font-serif"?: string;
	"font-mono"?: string;
	"shadow-color"?: string;
	"shadow-opacity"?: string;
	"shadow-blur"?: string;
	"shadow-spread"?: string;
	"shadow-offset-x"?: string;
	"shadow-offset-y"?: string;
	[key: string]: string | undefined;
}

export interface ThemeStyles {
	light: ThemeStyleProps;
	dark: ThemeStyleProps;
}

export interface ThemePreset {
	label: string;
	createdAt?: string;
	styles: {
		light: ThemeStyleProps;
		dark?: ThemeStyleProps;
	};
}

export interface ThemeState {
	currentMode: ThemeMode;
	preset: string;
	styles: ThemeStyles;
}

export type ColorFormat = "hsl" | "hex" | "rgb" | "oklch";

export interface ThemeProviderProps {
	children: React.ReactNode;
	defaultPreset?: string;
}
